import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import './ClientManagement.css'; // Ensure the path is correct
import { states, cities, nationalities } from './stateCityData'; // Assuming these are correctly exported

const countries = [
  { value: 'United States', label: 'United States' },
  { value: 'Canada', label: 'Canada' },
  { value: 'United Kingdom', label: 'United Kingdom' },
  // Add more countries as needed
];

const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    dateOfBirth: '',
    motherName: '',
    gender: '',
    religion: '',
    nationality: '',
    maritalStatus: '',
    nationalIdNumber: '',
    passportNumber: '',
    passportExpiryDate: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    country: '',
    phoneNumber: '',
    email: '',
    hasDisability: false,
    disabilityType: '',
    needsSpecialAssistance: false,
    assistanceType: '',
    hasPreviousMedicalCondition: false,
    medicalConditions: [],
    hasSurgeryInLastTwoYears: false,
    surgeryDetails: '',
    passengerCode: '',
    photo: '',
  });

  const [availableCities, setAvailableCities] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Calculate full name
  const fullName = `${clientForm.firstName} ${clientForm.middleName ? clientForm.middleName + ' ' : ''}${clientForm.lastName}`;

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setClientForm({
      ...clientForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleStateChange = (selectedOption) => {
    const state = selectedOption ? selectedOption.value : '';
    setClientForm({ ...clientForm, stateOrProvince: state, city: '' });
    setAvailableCities(cities[state] || []);
  };

  const handleCityChange = (selectedOption) => {
    setClientForm({ ...clientForm, city: selectedOption ? selectedOption.value : '' });
  };

  const handleMedicalConditionChange = (condition) => {
    setClientForm((prevState) => {
      const medicalConditions = prevState.medicalConditions.includes(condition)
        ? prevState.medicalConditions.filter((item) => item !== condition)
        : [...prevState.medicalConditions, condition];
      return { ...prevState, medicalConditions };
    });
  };

  const handleCountryChange = (selectedOption) => {
    setClientForm({ ...clientForm, country: selectedOption ? selectedOption.value : '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Include fullName in the payload
      await axios.post('http://localhost:5000/api/clients', { ...clientForm, fullName });
      setSuccessMessage('Client added successfully!');
      setClientForm({
        firstName: '',
        lastName: '',
        middleName: '',
        dateOfBirth: '',
        motherName: '',
        gender: '',
        religion: '',
        nationality: '',
        maritalStatus: '',
        nationalIdNumber: '',
        passportNumber: '',
        passportExpiryDate: '',
        address: '',
        city: '',
        stateOrProvince: '',
        postalCode: '',
        country: '',
        phoneNumber: '',
        email: '',
        hasDisability: false,
        disabilityType: '',
        needsSpecialAssistance: false,
        assistanceType: '',
        hasPreviousMedicalCondition: false,
        medicalConditions: [],
        hasSurgeryInLastTwoYears: false,
        surgeryDetails: '',
        passengerCode: '',
        photo: '',
      });
      setErrorMessage('');
    } catch (error) {
      console.error(error);
      setErrorMessage('Error saving client. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <div className="flex">
      <div className="container mx-auto px-4 py-8 bg-gray-100 flex-grow">
        <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
        {errorMessage && <p className="text-red-500 mb-4 text-center">{errorMessage}</p>}
        {successMessage && <p className="text-green-500 mb-4 text-center">{successMessage}</p>}
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
          {/* Personal Information */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">First Name</label>
              <input
                type="text"
                name="firstName"
                value={clientForm.firstName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Last Name</label>
              <input
                type="text"
                name="lastName"
                value={clientForm.lastName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Middle Name</label>
              <input
                type="text"
                name="middleName"
                value={clientForm.middleName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Full Name</label>
              <input
                type="text"
                value={fullName}
                readOnly
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none bg-gray-100 cursor-not-allowed"
              />
            </div>
          </div>

          {/* Other fields like Gender, Date of Birth, Nationality, State, etc... */}

          <div className="text-center mt-6">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Register Client
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ClientManagement;
