import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { states, cities } from './stateCityData'; // Import the dataset
import './ClientManagement.css'; // Import your CSS file

const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    dateOfBirth: '',
    gender: '',
    nationality: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    email: '',
    phoneNumber: '',
    passportNumber: '',
    passportExpirationDate: '',
    hasDiabetes: false,
    hasLowBloodPressure: false,
    hasHighBloodPressure: false,
    hasEpilepsy: false,
    hasHeartDisease: false,
    hasBreathingDifficulty: false,
    hasHadSurgery: false,
    surgeryDetails: ''
  });
  
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const [selectedState, setSelectedState] = useState(null);
  const [availableCities, setAvailableCities] = useState([]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setClientForm({
      ...clientForm,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleStateChange = (selectedOption) => {
    setSelectedState(selectedOption);
    setAvailableCities(cities[selectedOption.value] || []);
    setClientForm({ ...clientForm, stateOrProvince: selectedOption.value, city: '' });
  };

  const handleCityChange = (selectedOption) => {
    setClientForm({ ...clientForm, city: selectedOption.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/clients', clientForm);
      setSuccess('Client added successfully!');
      setClientForm({
        firstName: '',
        lastName: '',
        middleName: '',
        dateOfBirth: '',
        gender: '',
        nationality: '',
        address: '',
        city: '',
        stateOrProvince: '',
        postalCode: '',
        email: '',
        phoneNumber: '',
        passportNumber: '',
        passportExpirationDate: '',
        hasDiabetes: false,
        hasLowBloodPressure: false,
        hasHighBloodPressure: false,
        hasEpilepsy: false,
        hasHeartDisease: false,
        hasBreathingDifficulty: false,
        hasHadSurgery: false,
        surgeryDetails: ''
      });
      setError('');
    } catch (error) {
      setError('Error saving client');
      setSuccess('');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-100">
      <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
      {error && <p className="text-red-500 mb-4 text-center">{error}</p>}
      {success && <p className="text-green-500 mb-4 text-center">{success}</p>}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
        {/* Personal Information */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">First Name</label>
            <input
              type="text"
              name="firstName"
              value={clientForm.firstName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Last Name</label>
            <input
              type="text"
              name="lastName"
              value={clientForm.lastName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Date of Birth */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Date of Birth</label>
            <input
              type="date"
              name="dateOfBirth"
              value={clientForm.dateOfBirth}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
              min="1924-09-01"
              max="2024-09-30"
            />
          </div>
        </div>

        {/* Contact Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={clientForm.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Phone Number</label>
            <input
              type="tel"
              name="phoneNumber"
              value={clientForm.phoneNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Address Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Address</label>
            <input
              type="text"
              name="address"
              value={clientForm.address}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">City</label>
            <Select
              name="city"
              options={availableCities}
              onChange={handleCityChange}
              value={availableCities.find(city => city.value === clientForm.city)}
              className="w-full"
            />
          </div>
        </div>

        {/* State and Postal Code */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">State/Province</label>
            <Select
              name="stateOrProvince"
              options={states}
              onChange={handleStateChange}
              value={states.find(state => state.value === clientForm.stateOrProvince)}
              className="w-full"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Postal Code</label>
            <input
              type="text"
              name="postalCode"
              value={clientForm.postalCode}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Passport Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Number</label>
            <input
              type="text"
              name="passportNumber"
              value={clientForm.passportNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Expiration Date</label>
            <input
              type="date"
              name="passportExpirationDate"
              value={clientForm.passportExpirationDate}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
              min="2024-09-01"
              max="2034-09-30"
            />
          </div>
        </div>

        {/* Medical History */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Diabetes</label>
            <input
              type="checkbox"
              name="hasDiabetes"
              checked={clientForm.hasDiabetes}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Low Blood Pressure</label>
            <input
              type="checkbox"
              name="hasLowBloodPressure"
              checked={clientForm.hasLowBloodPressure}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has High Blood Pressure</label>
            <input
              type="checkbox"
              name="hasHighBloodPressure"
              checked={clientForm.hasHighBloodPressure}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Epilepsy</label>
            <input
              type="checkbox"
              name="hasEpilepsy"
              checked={clientForm.hasEpilepsy}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Heart Disease</label>
            <input
              type="checkbox"
              name="hasHeartDisease"
              checked={clientForm.hasHeartDisease}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Breathing Difficulty</label>
            <input
              type="checkbox"
              name="hasBreathingDifficulty"
              checked={clientForm.hasBreathingDifficulty}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Had Surgery</label>
            <input
              type="checkbox"
              name="hasHadSurgery"
              checked={clientForm.hasHadSurgery}
              onChange={handleChange}
              className="mr-2"
            />
          </div>
        </div>

        {clientForm.hasHadSurgery && (
          <div className="grid grid-cols-1 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Surgery Details</label>
              <textarea
                name="surgeryDetails"
                value={clientForm.surgeryDetails}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>
        )}

        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 w-full mt-4">
          Submit
        </button>
      </form>
    </div>
  );
};

export default ClientManagement;
