import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select'; // Import react-select
import './ClientManagement.css';
import stateCityData, { states } from './stateCityData'; // Import state and city data


const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    dateOfBirth: '',
    gender: '',
    nationality: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    email: '',
    phoneNumber: '',
    passportNumber: '',
    passportExpirationDate: '',
    hasDiabetes: false,
    hasLowBloodPressure: false,
    hasHighBloodPressure: false,
    hasEpilepsy: false,
    hasHeartDisease: false,
    hasBreathingDifficulty: false,
    hasHadSurgery: false,
    surgeryDetails: ''
  });
  
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [availableCities, setAvailableCities] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setClientForm({
      ...clientForm,
      [name]: value
    });

    // If state is changed, update available cities
    if (name === 'stateOrProvince') {
      setAvailableCities(stateCityData[value] || []);
      setClientForm({
        ...clientForm,
        city: '', // Reset city when state changes
      });
    }
  };

  const handleStateChange = (selectedOption) => {
    setClientForm({
      ...clientForm,
      stateOrProvince: selectedOption.value,
    });
    setAvailableCities(stateCityData[selectedOption.value] || []);
    setClientForm({
      ...clientForm,
      city: '', // Reset city when state changes
    });
  };

  const handleCityChange = (selectedOption) => {
    setClientForm({
      ...clientForm,
      city: selectedOption.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/clients', clientForm);
      setSuccess('Client added successfully!');
      setClientForm({
        firstName: '',
        lastName: '',
        middleName: '',
        dateOfBirth: '',
        gender: '',
        nationality: '',
        address: '',
        city: '',
        stateOrProvince: '',
        postalCode: '',
        email: '',
        phoneNumber: '',
        passportNumber: '',
        passportExpirationDate: '',
        hasDiabetes: false,
        hasLowBloodPressure: false,
        hasHighBloodPressure: false,
        hasEpilepsy: false,
        hasHeartDisease: false,
        hasBreathingDifficulty: false,
        hasHadSurgery: false,
        surgeryDetails: ''
      });
      setError('');
    } catch (error) {
      setError('Error saving client');
      setSuccess('');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-100">
      <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
      {error && <p className="text-red-500 mb-4 text-center">{error}</p>}
      {success && <p className="text-green-500 mb-4 text-center">{success}</p>}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
        
        {/* Personal Information */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">First Name</label>
            <input
              type="text"
              name="firstName"
              value={clientForm.firstName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Last Name</label>
            <input
              type="text"
              name="lastName"
              value={clientForm.lastName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Middle Name</label>
            <input
              type="text"
              name="middleName"
              value={clientForm.middleName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
            />
          </div>
        </div>

        {/* Date of Birth */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Date of Birth</label>
            <input
              type="date"
              name="dateOfBirth"
              value={clientForm.dateOfBirth}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Nationality */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Nationality</label>
            <Select
              options={nationalities}
              onChange={(option) => setClientForm({ ...clientForm, nationality: option.value })}
              className="basic-single"
              classNamePrefix="select"
            />
          </div>
        </div>

        {/* State and City Selection */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">State/Province</label>
            <Select
              options={states}
              onChange={handleStateChange}
              className="basic-single"
              classNamePrefix="select"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">City</label>
            <Select
              options={availableCities.map(city => ({ value: city, label: city }))}
              onChange={handleCityChange}
              className="basic-single"
              classNamePrefix="select"
              isDisabled={availableCities.length === 0} // Disable if no cities are available
            />
          </div>
        </div>

        {/* Address Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Address</label>
            <input
              type="text"
              name="address"
              value={clientForm.address}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Postal Code</label>
            <input
              type="text"
              name="postalCode"
              value={clientForm.postalCode}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Contact Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={clientForm.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Phone Number</label>
            <input
              type="tel"
              name="phoneNumber"
              value={clientForm.phoneNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Passport Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Number</label>
            <input
              type="text"
              name="passportNumber"
              value={clientForm.passportNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Expiration Date</label>
            <input
              type="date"
              name="passportExpirationDate"
              value={clientForm.passportExpirationDate}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Medical History */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Diabetes?</label>
            <input
              type="checkbox"
              name="hasDiabetes"
              checked={clientForm.hasDiabetes}
              onChange={(e) => setClientForm({ ...clientForm, hasDiabetes: e.target.checked })}
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Has Low Blood Pressure?</label>
            <input
              type="checkbox"
              name="hasLowBloodPressure"
              checked={clientForm.hasLowBloodPressure}
              onChange={(e) => setClientForm({ ...clientForm, hasLowBloodPressure: e.target.checked })}
            />
          </div>
        </div>

        {/* Submit Button */}
        <div className="flex justify-center mt-6">
          <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg">Submit</button>
        </div>
      </form>
    </div>
  );
};

export default ClientManagement;
