import React, { useState, useEffect } from "react";
import axios from "axios";

const AddDoctor = () => {
  const [doctorDetails, setDoctorDetails] = useState({
    doctorCode: "",
    name: "",
    nationalId: "",
    specialty: "",
    password: "",
    username: "",
    appointmentDays: [],
    appointmentTimes: [],
    clinicId: "", // Store the selected clinic ID
  });
  const [clinics, setClinics] = useState([]); // Store the list of clinics
  const [message, setMessage] = useState("");

  // Fetch clinics from the backend when the component loads
  useEffect(() => {
    const fetchClinics = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/clinics");
        setClinics(response.data); // Set the fetched clinics to state
      } catch (error) {
        console.error("Error fetching clinics:", error);
      }
    };

    fetchClinics();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/doctors/add", doctorDetails);
      setMessage(response.data.message);
      // Reset form
      setDoctorDetails({
        doctorCode: "",
        name: "",
        nationalId: "",
        specialty: "",
        password: "",
        username: "",
        appointmentDays: [],
        appointmentTimes: [],
        clinicId: "", // Reset selected clinic
      });
    } catch (error) {
      setMessage("Error adding doctor: " + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div>
      <h1>Add Doctor</h1>
      <form onSubmit={handleSubmit}>
        {/* Clinic Selection Dropdown */}
        <div>
          <label>Select Clinic</label>
          <select
            value={doctorDetails.clinicId}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, clinicId: e.target.value })}
            required
          >
            <option value="">Select a Clinic</option>
            {clinics.map((clinic) => (
              <option key={clinic._id} value={clinic._id}>
                {clinic.name}
              </option>
            ))}
          </select>
        </div>

        {/* Doctor Code */}
        <div>
          <label>Doctor Code</label>
          <input
            type="text"
            placeholder="Doctor Code"
            value={doctorDetails.doctorCode}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, doctorCode: e.target.value })}
            required
          />
        </div>

        {/* Name */}
        <div>
          <label>Doctor Name</label>
          <input
            type="text"
            placeholder="Doctor Name"
            value={doctorDetails.name}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, name: e.target.value })}
            required
          />
        </div>

        {/* National ID */}
        <div>
          <label>National ID</label>
          <input
            type="text"
            placeholder="National ID"
            value={doctorDetails.nationalId}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, nationalId: e.target.value })}
            required
          />
        </div>

        {/* Specialty */}
        <div>
          <label>Specialty</label>
          <input
            type="text"
            placeholder="Specialty"
            value={doctorDetails.specialty}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, specialty: e.target.value })}
            required
          />
        </div>

        {/* Password */}
        <div>
          <label>Password</label>
          <input
            type="password"
            placeholder="Password"
            value={doctorDetails.password}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, password: e.target.value })}
            required
          />
        </div>

        {/* Username */}
        <div>
          <label>Username</label>
          <input
            type="text"
            placeholder="Username"
            value={doctorDetails.username}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, username: e.target.value })}
            required
          />
        </div>

        {/* Submit Button */}
        <button type="submit">Add Doctor</button>
      </form>

      {/* Success or Error Message */}
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddDoctor;
