import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AddDoctor.css"; // Importing the CSS file

const AddDoctor = () => {
  const [doctorDetails, setDoctorDetails] = useState({
    doctorCode: "",
    name: "",
    nationalId: "",
    specialty: "",
    password: "",
    username: "",
    appointmentDays: [],
    appointmentTimes: [], // Assuming time slots as an array of strings
    clinicId: "",
  });
  const [clinics, setClinics] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchClinics = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/clinics");
        setClinics(response.data);
      } catch (error) {
        console.error("Error fetching clinics:", error);
      }
    };

    fetchClinics();
  }, []);

  const handleTimeChange = (e, index) => {
    const updatedTimes = [...doctorDetails.appointmentTimes];
    updatedTimes[index] = e.target.value;
    setDoctorDetails({ ...doctorDetails, appointmentTimes: updatedTimes });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/doctors/add", doctorDetails);
      setMessage(response.data.message);
      setDoctorDetails({
        doctorCode: "",
        name: "",
        nationalId: "",
        specialty: "",
        password: "",
        username: "",
        appointmentDays: [],
        appointmentTimes: [],
        clinicId: "",
      });
    } catch (error) {
      setMessage("Error adding doctor: " + (error.response?.data?.message || error.message));
    }
  };

  return (
    <div className="container">
      <h1>Add Doctor</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Select Clinic</label>
          <select
            value={doctorDetails.clinicId}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, clinicId: e.target.value })}
            required
          >
            <option value="">Select a Clinic</option>
            {clinics.map((clinic) => (
              <option key={clinic._id} value={clinic._id}>
                {clinic.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Doctor Code</label>
          <input
            type="text"
            placeholder="Doctor Code"
            value={doctorDetails.doctorCode}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, doctorCode: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Doctor Name</label>
          <input
            type="text"
            placeholder="Doctor Name"
            value={doctorDetails.name}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, name: e.target.value })}
            required
          />
        </div>

        <div>
          <label>National ID</label>
          <input
            type="text"
            placeholder="National ID"
            value={doctorDetails.nationalId}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, nationalId: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Specialty</label>
          <input
            type="text"
            placeholder="Specialty"
            value={doctorDetails.specialty}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, specialty: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Password</label>
          <input
            type="password"
            placeholder="Password"
            value={doctorDetails.password}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, password: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Username</label>
          <input
            type="text"
            placeholder="Username"
            value={doctorDetails.username}
            onChange={(e) => setDoctorDetails({ ...doctorDetails, username: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Appointment Days</label>
          <div>
            <input type="checkbox" id="monday" name="appointmentDays" value="Monday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="monday">Monday</label>
            <input type="checkbox" id="tuesday" name="appointmentDays" value="Tuesday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="tuesday">Tuesday</label>
            <input type="checkbox" id="wednesday" name="appointmentDays" value="Wednesday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="wednesday">Wednesday</label>
            <input type="checkbox" id="thursday" name="appointmentDays" value="Thursday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="thursday">Thursday</label>
            <input type="checkbox" id="friday" name="appointmentDays" value="Friday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="friday">Friday</label>
            <input type="checkbox" id="saturday" name="appointmentDays" value="Saturday" onChange={(e) => handleAppointmentDayChange(e)} />
            <label htmlFor="saturday">Saturday</label>
          </div>
        </div>

        <div>
          <label>Appointment Times</label>
          {doctorDetails.appointmentDays.map((day, index) => (
            <div key={index}>
              <label>{day}</label>
              <input type="time" value={doctorDetails.appointmentTimes[index] || ''} onChange={(e) => handleTimeChange(e, index)} />
            </div>
          ))}
        </div>

        <button type="submit">Add Doctor</button>
      </form>

      {message && <p className={`message ${message.includes('Error') ? 'error' : 'success'}`}>{message}</p>}
    </div>
  );
};

export default AddDoctor;
