import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import './ClientManagement.css'; // Ensure the path is correct
import { states, cities, nationalities } from './stateCityData'; // Assuming these are correctly exported



const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    fullName:"",
    dateOfBirth: '',
    motherName: '',
    gender: '',
    religion: '',
    nationality: '',
    maritalStatus: '',
    nationalIdNumber: '',
    passportNumber: '',
    passportExpiryDate: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    country: '',
    phoneNumber: '',
    email: '',
    hasDisability: false,
    disabilityType: '',
    needsSpecialAssistance: false,
    assistanceType: '',
    hasPreviousMedicalCondition: false,
    medicalConditions: [],
    hasSurgeryInLastTwoYears: false,
    surgeryDetails: '',
    passengerCode: '',
    photo: '',
  });

  const [availableCities, setAvailableCities] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setClientForm({
      ...clientForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleStateChange = (selectedOption) => {
    const state = selectedOption ? selectedOption.value : '';
    setClientForm({ ...clientForm, stateOrProvince: state, city: '' });
    setAvailableCities(cities[state] || []);
  };

  const handleCityChange = (selectedOption) => {
    setClientForm({ ...clientForm, city: selectedOption ? selectedOption.value : '' });
  };

  const handleMedicalConditionChange = (condition) => {
    setClientForm((prevState) => {
      const medicalConditions = prevState.medicalConditions.includes(condition)
        ? prevState.medicalConditions.filter((item) => item !== condition)
        : [...prevState.medicalConditions, condition];
      return { ...prevState, medicalConditions };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/clients', clientForm);
      setSuccessMessage('Client added successfully!');
      setClientForm({
        firstName: '',
        lastName: '',
        middleName: '',
        dateOfBirth: '',
        motherName: '',
        gender: '',
        religion: '',
        nationality: '',
        maritalStatus: '',
        nationalIdNumber: '',
        passportNumber: '',
        passportExpiryDate: '',
        address: '',
        city: '',
        stateOrProvince: '',
        postalCode: '',
        country: '',
        phoneNumber: '',
        email: '',
        hasDisability: false,
        disabilityType: '',
        needsSpecialAssistance: false,
        assistanceType: '',
        hasPreviousMedicalCondition: false,
        medicalConditions: [],
        hasSurgeryInLastTwoYears: false,
        surgeryDetails: '',
        passengerCode: '',
        photo: '',
      });
      setErrorMessage('');
    } catch (error) {
      console.error(error);
      setErrorMessage('Error saving client. Please try again.');
      setSuccessMessage('');
    }
  };

  return (
    <div className="flex">
      <div className="container mx-auto px-4 py-8 bg-gray-100 flex-grow">
        <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
        {errorMessage && <p className="text-red-500 mb-4 text-center">{errorMessage}</p>}
        {successMessage && <p className="text-green-500 mb-4 text-center">{successMessage}</p>}
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
          {/* Personal Information */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">First Name</label>
              <input
                type="text"
                name="firstName"
                value={clientForm.firstName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Middle Name</label>
              <input
                type="text"
                name="middleName"
                value={clientForm.middleName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Last Name</label>
              <input
                type="text"
                name="lastName"
                value={clientForm.lastName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Full Name</label>
              <input
                type="text"
                name="lastName"
                value={clientForm.fullName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
           
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Date of Birth</label>
              <input
                type="date"
                name="dateOfBirth"
                value={clientForm.dateOfBirth}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Mother's Name</label>
              <input
                type="text"
                name="motherName"
                value={clientForm.motherName}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>

          {/* Gender */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Gender</label>
              <div className="flex items-center">
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={clientForm.gender === 'male'}
                  onChange={handleChange}
                  className="mr-2"
                />
                <label className="mr-4 text-gray-700">Male</label>
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={clientForm.gender === 'female'}
                  onChange={handleChange}
                  className="mr-2"
                />
                <label className="text-gray-700">Female</label>
              </div>
            </div>
          </div>

          {/* Religion */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Religion</label>
              <input
                type="text"
                name="religion"
                value={clientForm.religion}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>

          {/* Nationality */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Nationality</label>
              <select
                name="nationality"
                value={clientForm.nationality}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              >
                {nationalities.map((nationality) => (
                  <option key={nationality.value} value={nationality.value}>
                    {nationality.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Contact Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Email</label>
              <input
                type="email"
                name="email"
                value={clientForm.email}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Phone Number</label>
              <input
                type="tel"
                name="phoneNumber"
                value={clientForm.phoneNumber}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>

          {/* Address */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">Address</label>
              <input
                type="text"
                name="address"
                value={clientForm.address}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2"></label>
              <input
                type="file"
                name="address"
                value={clientForm.photo}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
                required
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">City</label>
              <Select
                options={availableCities.map((city) => ({ value: city, label: city }))}
                onChange={handleCityChange}
                value={availableCities.find((city) => city.value === clientForm.city)}
                className="w-full"
              />
            </div>
            <div className="col-span-1">
              <label className="block text-gray-700 font-bold mb-2">State/Province</label>
              <select name="stateOrProvince" onChange={handleStateChange} className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500">
                {states.map((state) => (
                  <option key={state.value} value={state.value}>
                    {state.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

 

          {/* Medical Information */}
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Has Disability</label>
            <input
              type="checkbox"
              name="hasDisability"
              checked={clientForm.hasDisability}
              onChange={handleChange}
            />
          </div>

          {clientForm.hasDisability && (
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">Disability Type</label>
              <input
                type="text"
                name="disabilityType"
                value={clientForm.disabilityType}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          )}

          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Needs Special Assistance</label>
            <input
              type="checkbox"
              name="needsSpecialAssistance"
              checked={clientForm.needsSpecialAssistance}
              onChange={handleChange}
            />
          </div>

          {clientForm.needsSpecialAssistance && (
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">Assistance Type</label>
              <input
                type="text"
                name="assistanceType"
                value={clientForm.assistanceType}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          )}

          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Has Previous Medical Condition</label>
            <input
              type="checkbox"
              name="hasPreviousMedicalCondition"
              checked={clientForm.hasPreviousMedicalCondition}
              onChange={handleChange}
            />
          </div>

          {clientForm.hasPreviousMedicalCondition && (
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">Medical Conditions</label>
              <div className="flex flex-wrap">
                {['Diabetes', 'Hypertension', 'Asthma'].map((condition) => (
                  <div key={condition} className="mr-4">
                    <input
                      type="checkbox"
                      checked={clientForm.medicalConditions.includes(condition)}
                      onChange={() => handleMedicalConditionChange(condition)}
                    />
                    <label className="ml-2">{condition}</label>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Has Surgery in Last Two Years</label>
            <input
              type="checkbox"
              name="hasSurgeryInLastTwoYears"
              checked={clientForm.hasSurgeryInLastTwoYears}
              onChange={handleChange}
            />
          </div>

          {clientForm.hasSurgeryInLastTwoYears && (
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2">Surgery Details</label>
              <input
                type="text"
                name="surgeryDetails"
                value={clientForm.surgeryDetails}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              />
            </div>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            className="mt-4 w-full bg-blue-600 text-white font-bold py-2 rounded-lg hover:bg-blue-500"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default ClientManagement;
