import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { Cloudinary } from 'cloudinary-core';
import { states, cities, nationalities } from './stateCityData';
import './ClientManagement.css';

const countries = [
  { name: 'United States' },
  { name: 'Canada' },
  { name: 'United Kingdom' },
  // Add more countries as needed
];

const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    dateOfBirth: '',
    motherName: '',
    gender: '',
    religion: '',
    nationality: '',
    maritalStatus: '',
    nationalIdNumber: '',
    passportNumber: '',
    passportExpiryDate: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    country: '',
    phoneNumber: '',
    email: '',
    hasDisability: false,
    disabilityType: '',
    needsSpecialAssistance: false,
    assistanceType: '',
    hasPreviousMedicalCondition: false,
    medicalConditions: [],
    hasSurgeryInLastTwoYears: false,
    surgeryDetails: '',
    passengerCode: '',
    photo: '', // This will store the Cloudinary image URL
  });

  const [availableCities, setAvailableCities] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [photoFile, setPhotoFile] = useState(null);

  const cloudinary = new Cloudinary({ cloud_name: 'YOUR_CLOUD_NAME' });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setClientForm({
      ...clientForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handlePhotoChange = (e) => {
    setPhotoFile(e.target.files[0]); // Store the selected file in state
  };

  const uploadPhoto = async () => {
    if (photoFile) {
      const formData = new FormData();
      formData.append('file', photoFile);
      formData.append('upload_preset', 'YOUR_UPLOAD_PRESET'); // Set up upload preset in Cloudinary

      try {
        const response = await axios.post(
          'https://api.cloudinary.com/v1_1/YOUR_CLOUD_NAME/image/upload',
          formData
        );
        return response.data.secure_url; // Return the uploaded image's URL
      } catch (error) {
        console.error('Error uploading photo:', error);
        return null;
      }
    }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const uploadedPhotoUrl = await uploadPhoto();

    if (uploadedPhotoUrl) {
      const formDataWithPhoto = {
        ...clientForm,
        photo: uploadedPhotoUrl, // Add the Cloudinary image URL to the form data
      };

      try {
        await axios.post('http://localhost:5000/api/clients', formDataWithPhoto);
        setSuccessMessage('Client added successfully!');
        setErrorMessage('');
      } catch (error) {
        console.error(error);
        setErrorMessage('Error saving client. Please try again.');
        setSuccessMessage('');
      }
    } else {
      setErrorMessage('Photo upload failed. Please try again.');
    }
  };

  return (
    <div className="flex">
      <div className="container mx-auto px-4 py-8 bg-gray-100 flex-grow">
        <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
        {errorMessage && <p className="text-red-500 mb-4 text-center">{errorMessage}</p>}
        {successMessage && <p className="text-green-500 mb-4 text-center">{successMessage}</p>}
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
          {/* Form fields */}
          <div className="mb-4">
            <label className="block text-gray-700 font-bold mb-2">Upload Photo</label>
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
            />
          </div>
          {/* Submit button */}
          <button
            type="submit"
            className="mt-4 w-full bg-blue-600 text-white font-bold py-2 rounded-lg hover:bg-blue-500"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default ClientManagement;
