import React, { useState } from 'react';
import axios from 'axios';
import Select from 'react-select';
import './ClientManagement.css'; // Ensure the path is correct
import { states, cities, nationalities } from './stateCityData'; // Assuming these are correctly exported

const ClientManagement = () => {
  const [clientForm, setClientForm] = useState({
    firstName: '',
    lastName: '',
    middleName: '',
    dateOfBirth: '',
    gender: '',
    nationality: '',
    address: '',
    city: '',
    stateOrProvince: '',
    postalCode: '',
    email: '',
    phoneNumber: '',
    passportNumber: '',
    passportExpirationDate: '',
    hasDiabetes: false,
    hasLowBloodPressure: false,
    hasHighBloodPressure: false,
    hasEpilepsy: false,
    hasHeartDisease: false,
    hasBreathingDifficulty: false,
    hasHadSurgery: false,
    surgeryDetails: ''
  });

  const [availableCities, setAvailableCities] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setClientForm({
      ...clientForm,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleStateChange = (selectedOption) => {
    const state = selectedOption ? selectedOption.value : '';
    setClientForm({ ...clientForm, stateOrProvince: state, city: '' });
    setAvailableCities(cities[state] || []); // Update city options based on selected state
  };

  const handleCityChange = (selectedOption) => {
    setClientForm({ ...clientForm, city: selectedOption ? selectedOption.value : '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/clients', clientForm);
      setSuccess('Client added successfully!');
      setClientForm({
        firstName: '',
        lastName: '',
        middleName: '',
        dateOfBirth: '',
        gender: '',
        nationality: '',
        address: '',
        city: '',
        stateOrProvince: '',
        postalCode: '',
        email: '',
        phoneNumber: '',
        passportNumber: '',
        passportExpirationDate: '',
        hasDiabetes: false,
        hasLowBloodPressure: false,
        hasHighBloodPressure: false,
        hasEpilepsy: false,
        hasHeartDisease: false,
        hasBreathingDifficulty: false,
        hasHadSurgery: false,
        surgeryDetails: ''
      });
      setError('');
    } catch (error) {
      console.error(error); // Log error for debugging
      setError('Error saving client');
      setSuccess('');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-100">
      <h1 className="text-3xl font-bold mb-4 text-center text-blue-600">Registration Form</h1>
      {error && <p className="text-red-500 mb-4 text-center">{error}</p>}
      {success && <p className="text-green-500 mb-4 text-center">{success}</p>}
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md">
        {/* Personal Information */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">First Name</label>
            <input
              type="text"
              name="firstName"
              value={clientForm.firstName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Last Name</label>
            <input
              type="text"
              name="lastName"
              value={clientForm.lastName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Middle Name</label>
            <input
              type="text"
              name="middleName"
              value={clientForm.middleName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
            />
          </div>
        </div>

        {/* Date of Birth */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Date of Birth</label>
            <input
              type="date"
              name="dateOfBirth"
              value={clientForm.dateOfBirth}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Nationality */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Nationality</label>
            <select
              name="nationality"
              value={clientForm.nationality}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            >
              {nationalities.map((nationality) => (
                <option key={nationality.value} value={nationality.value}>
                  {nationality.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        {/* Contact Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={clientForm.email}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Phone Number</label>
            <input
              type="tel"
              name="phoneNumber"
              value={clientForm.phoneNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Address Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Address</label>
            <input
              type="text"
              name="address"
              value={clientForm.address}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">City</label>
            <Select
              name="city"
              options={availableCities}
              value={availableCities.find(city => city.value === clientForm.city)}
              onChange={handleCityChange}
              className="w-full"
              placeholder="Select City"
              required
            />
          </div>
        </div>

        {/* State and Postal Code */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">State/Province</label>
            <Select
              name="stateOrProvince"
              options={states}
              onChange={handleStateChange}
              className="w-full"
              placeholder="Select State"
              required
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Postal Code</label>
            <input
              type="text"
              name="postalCode"
              value={clientForm.postalCode}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
              required
            />
          </div>
        </div>

        {/* Passport Information */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Number</label>
            <input
              type="text"
              name="passportNumber"
              value={clientForm.passportNumber}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="col-span-1">
            <label className="block text-gray-700 font-bold mb-2">Passport Expiration Date</label>
            <input
              type="date"
              name="passportExpirationDate"
              value={clientForm.passportExpirationDate}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
            />
          </div>
        </div>

        {/* Medical History */}
        <h2 className="text-xl font-bold mb-4">Medical History</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {['hasDiabetes', 'hasLowBloodPressure', 'hasHighBloodPressure', 'hasEpilepsy', 'hasHeartDisease', 'hasBreathingDifficulty', 'hasHadSurgery'].map(condition => (
            <div className="col-span-1 flex items-center" key={condition}>
              <input
                type="checkbox"
                name={condition}
                checked={clientForm[condition]}
                onChange={handleChange}
                className="mr-2"
              />
              <label className="text-gray-700">{condition.replace(/([A-Z])/g, ' $1').toLowerCase()}</label>
            </div>
          ))}
        </div>

        {/* Surgery Details */}
        <div className="grid grid-cols-1">
          <label className="block text-gray-700 font-bold mb-2">Surgery Details</label>
          <textarea
            name="surgeryDetails"
            value={clientForm.surgeryDetails}
            onChange={handleChange}
            className="w-full h-24 px-3 py-2 border rounded-lg border-gray-300 focus:outline-none focus:border-blue-500"
          />
        </div>

        {/* Submit Button */}
        <div className="grid grid-cols-1">
          <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default ClientManagement;
